SELECT * FROM (SELECT rownum R, D.* FROM (
SELECT * FROM ANIMAL
) D) B WHERE B.R BETWEEN #{startRow} AND #{endRow}

SELECT * FROM (SELECT rownum R, D.* FROM (
select * from animal where animal_species like '%'||#{animal_species}||'%' and animal_gender like '%'||#{animal_gender}||'%' and (animal_weight >= #{animal_weight_over} and animal_weight < #{animal_weight_under}) and (animal_age_num >= #{animal_age_more} and animal_age_num <= #{animal_age_less}) and animal_name like '%'||#{animal_name}||'%'
) D) B WHERE B.R BETWEEN #{startRow} AND #{endRow}
